import React, { useEffect, useMemo, useState } from 'react';
import NavBar from '../components/NavBar';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { useAuth } from '../context/AuthContext';

const incomeCategories = [
  { value: 'salary', label: 'Salary' },
  { value: 'bonus', label: 'Bonus' },
  { value: 'freelance', label: 'Freelance' },
  { value: 'investment', label: 'Investment' },
  { value: 'other-income', label: 'Other income' },
];

const expenseCategories = [
  { value: 'groceries', label: 'Groceries' },
  { value: 'rent', label: 'Rent' },
  { value: 'utilities', label: 'Utilities' },
  { value: 'transport', label: 'Transport' },
  { value: 'entertainment', label: 'Entertainment' },
  { value: 'health', label: 'Health' },
  { value: 'other-expense', label: 'Other expense' },
];

function Dashboard() {
  const { token } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [transactions, setTransactions] = useState([]);

  const [incomeAmount, setIncomeAmount] = useState('');
  const [incomeCategory, setIncomeCategory] = useState('salary');
  const [incomeNote, setIncomeNote] = useState('');

  const [expenseAmount, setExpenseAmount] = useState('');
  const [expenseCategory, setExpenseCategory] = useState('groceries');
  const [expenseNote, setExpenseNote] = useState('');

  const authHeaders = useMemo(() => ({
    'Content-Type': 'application/json',
    ...(token ? { Authorization: `Bearer ${token}` } : {}),
  }), [token]);

  const fetchTransactions = async () => {
    try {
      const res = await fetch('/api/transactions', { headers: authHeaders });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.message || 'Failed to load transactions');
      setTransactions(data);
    } catch (e) {
      setError(e.message);
    }
  };

  useEffect(() => {
    fetchTransactions();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token]);

  const handleAdd = async ({ type, amount, category, note }) => {
    setError('');
    setSuccess('');
    setLoading(true);
    try {
      const res = await fetch('/api/transactions', {
        method: 'POST',
        headers: authHeaders,
        body: JSON.stringify({ type, amount: Number(amount), category, note }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.message || 'Failed to add transaction');
      setSuccess('Saved successfully');
      setTransactions((prev) => [data, ...prev]);
      // reset the right form
      if (type === 'income') {
        setIncomeAmount('');
        setIncomeNote('');
      } else {
        setExpenseAmount('');
        setExpenseNote('');
      }
      setTimeout(() => setSuccess(''), 2000);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="relative min-h-screen w-full bg-[#F9FAFB]">
      <NavBar />
      <div className="relative mx-auto max-w-6xl px-4 py-8">
        <div className="grid grid-cols-1 md:grid-cols-[240px_1fr] gap-6">
          <Sidebar />

          <main>
            <h1 className="text-2xl font-semibold text-[#111827]">Dashboard</h1>

            {error ? (
              <div className="mt-4 rounded-lg bg-red-50 border border-red-200 text-red-700 px-3 py-2 text-sm">{error}</div>
            ) : null}
            {success ? (
              <div className="mt-4 rounded-lg bg-emerald-50 border border-emerald-200 text-emerald-700 px-3 py-2 text-sm">{success}</div>
            ) : null}

            <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Income card */}
              <div className="rounded-2xl border border-gray-200 bg-white p-5 shadow-sm">
                <h2 className="text-lg font-medium text-[#111827]">Add income</h2>
                <form
                  onSubmit={(e) => { e.preventDefault(); handleAdd({ type: 'income', amount: incomeAmount, category: incomeCategory, note: incomeNote }); }}
                  className="mt-4 space-y-3"
                >
                  <div>
                    <label className="block text-sm text-[#374151] mb-1">Amount</label>
                    <input type="number" step="0.01" required value={incomeAmount} onChange={(e) => setIncomeAmount(e.target.value)} className="w-full rounded-lg border border-gray-200 bg-white text-[#111827] placeholder-gray-400 px-3 py-2 outline-none focus:ring-2 focus:ring-[#7C3AED]/30 focus:border-[#7C3AED]/60 transition" placeholder="0.00" />
                  </div>
                  <div>
                    <label className="block text-sm text-[#374151] mb-1">Category</label>
                    <select value={incomeCategory} onChange={(e) => setIncomeCategory(e.target.value)} className="w-full rounded-lg border border-gray-200 bg-white text-[#111827] px-3 py-2 outline-none focus:ring-2 focus:ring-[#7C3AED]/30 focus:border-[#7C3AED]/60 transition">
                      {incomeCategories.map((c) => (
                        <option key={c.value} value={c.value}>{c.label}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm text-[#374151] mb-1">Note (optional)</label>
                    <input type="text" value={incomeNote} onChange={(e) => setIncomeNote(e.target.value)} className="w-full rounded-lg border border-gray-200 bg-white text-[#111827] placeholder-gray-400 px-3 py-2 outline-none focus:ring-2 focus:ring-[#7C3AED]/30 focus:border-[#7C3AED]/60 transition" placeholder="Salary for Sep" />
                  </div>
                  <button type="submit" disabled={loading} className="w-full mt-2 rounded-lg bg-[#7C3AED] text-white font-medium py-2.5 shadow-sm hover:brightness-110 active:brightness-95 transition disabled:opacity-70 disabled:cursor-not-allowed">Add income</button>
                </form>
              </div>

              {/* Expense card */}
              <div className="rounded-2xl border border-gray-200 bg-white p-5 shadow-sm">
                <h2 className="text-lg font-medium text-[#111827]">Add expense</h2>
                <form
                  onSubmit={(e) => { e.preventDefault(); handleAdd({ type: 'expense', amount: expenseAmount, category: expenseCategory, note: expenseNote }); }}
                  className="mt-4 space-y-3"
                >
                  <div>
                    <label className="block text-sm text-[#374151] mb-1">Amount</label>
                    <input type="number" step="0.01" required value={expenseAmount} onChange={(e) => setExpenseAmount(e.target.value)} className="w-full rounded-lg border border-gray-200 bg-white text-[#111827] placeholder-gray-400 px-3 py-2 outline-none focus:ring-2 focus:ring-[#7C3AED]/30 focus:border-[#7C3AED]/60 transition" placeholder="0.00" />
                  </div>
                  <div>
                    <label className="block text-sm text-[#374151] mb-1">Category</label>
                    <select value={expenseCategory} onChange={(e) => setExpenseCategory(e.target.value)} className="w-full rounded-lg border border-gray-200 bg-white text-[#111827] px-3 py-2 outline-none focus:ring-2 focus:ring-[#7C3AED]/30 focus:border-[#7C3AED]/60 transition">
                      {expenseCategories.map((c) => (
                        <option key={c.value} value={c.value}>{c.label}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm text-[#374151] mb-1">Note (optional)</label>
                    <input type="text" value={expenseNote} onChange={(e) => setExpenseNote(e.target.value)} className="w-full rounded-lg border border-gray-200 bg-white text-[#111827] placeholder-gray-400 px-3 py-2 outline-none focus:ring-2 focus:ring-[#7C3AED]/30 focus:border-[#7C3AED]/60 transition" placeholder="Groceries, rent, etc." />
                  </div>
                  <button type="submit" disabled={loading} className="w-full mt-2 rounded-lg bg-[#0D9488] text-white font-medium py-2.5 shadow-sm hover:brightness-110 active:brightness-95 transition disabled:opacity-70 disabled:cursor-not-allowed">Add expense</button>
                </form>
              </div>
            </div>

            {/* Recent transactions */}
            <section className="mt-8 rounded-2xl border border-gray-200 bg-white p-6 shadow-sm">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-[#111827]">Recent transactions</h3>
              </div>
              <div className="mt-4 divide-y divide-gray-100">
                {transactions.length === 0 ? (
                  <p className="text-sm text-[#6B7280]">No transactions yet.</p>
                ) : (
                  transactions.slice(0, 8).map((t) => (
                    <div key={t._id} className="py-3 flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <span className={
                          'inline-flex items-center justify-center w-8 h-8 rounded-lg text-xs font-medium ' +
                          (t.type === 'income' ? 'bg-emerald-100 text-emerald-700' : 'bg-rose-100 text-rose-700')
                        }>
                          {t.type === 'income' ? '+' : '-'}
                        </span>
                        <div>
                          <div className="text-sm font-medium text-[#111827] capitalize">{t.category.replace('-', ' ')}</div>
                          {t.note ? <div className="text-xs text-[#6B7280]">{t.note}</div> : null}
                        </div>
                      </div>
                      <div className="text-sm font-semibold text-[#111827]">{t.type === 'income' ? '+' : '-'}${Number(t.amount).toFixed(2)}</div>
                    </div>
                  ))
                )}
              </div>
            </section>
          </main>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Dashboard;
