import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const items = [
  { to: '/', label: 'Home' },
  { to: '/dashboard', label: 'Dashboard' },
  { to: '/transactions', label: 'Transactions' },
  { to: '/reports', label: 'Reports' },
  { to: '/profile', label: 'Profile', icon: 'ðŸ‘¤' },
];

const Sidebar = () => {
  const location = useLocation();
  const mainItems = items.slice(0, -1); // All items except profile
  const profileItem = items[items.length - 1]; // Profile item
  
  return (
    <aside className="hidden md:block w-60 shrink-0">
      <div className="sticky top-14 h-[calc(100vh-56px)] p-4 flex flex-col">
        <div className="space-y-2 flex-1">
          {mainItems.map((item) => {
            const active = location.pathname === item.to;
            return (
              <Link
                key={item.to}
                to={item.to}
                className={
                  `block rounded-lg px-3 py-2 transition ` +
                  (active
                    ? 'bg-[#FD980C] text-black font-medium'
                    : 'text-[#7C3AED]  hover:bg-white/5 border border-white/5')
                }
              >
                {item.label}
              </Link>
            );
          })}
        </div>
        
        {/* Profile at the bottom */}
        <div className="mt-auto pt-4 border-t border-gray-200">
          <Link
            to={profileItem.to}
            className={
              `flex items-center gap-2 rounded-lg px-3 py-2 transition ` +
              (location.pathname === profileItem.to
                ? 'bg-[#FD980C] text-black font-medium'
                : 'text-[#7C3AED]  hover:bg-white/5 border border-white/5')
            }
          >
            <span>{profileItem.icon}</span>
            {profileItem.label}
          </Link>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
