import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const res = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (!res.ok) {
        throw new Error(data?.message || 'Login failed');
      }
      if (data?.token) {
        localStorage.setItem('token', data.token);
      }
      navigate('/');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen w-full bg-[#F9FAFB]">
      <div className="mx-auto max-w-6xl grid grid-cols-1 md:grid-cols-2 min-h-screen">
        {/* Left welcome panel */}
        <div className="relative p-10 flex flex-col justify-center overflow-hidden">
          <div className="absolute -top-20 -left-20 w-96 h-96 bg-[#7C3AED]/10 rounded-full blur-3xl animate-float" />
          <div className="absolute -bottom-24 -right-24 w-96 h-96 bg-[#0D9488]/10 rounded-full blur-3xl animate-float" style={{ animationDelay: '400ms' }} />
          <div className="relative">
            <div className="inline-flex items-center gap-2 rounded-full bg-[#7C3AED]/10 text-[#7C3AED] px-3 py-1 text-sm font-medium">
              Welcome to
            </div>
            <h1 className="mt-4 text-4xl md:text-5xl font-bold text-[#111827]">ExpenseEase</h1>
            <p className="mt-3 text-[#374151] text-lg max-w-md">
              Manage your spending effortlessly. Track expenses, visualize trends, and stay in control with clarity.
            </p>
            <div className="mt-6 flex items-center gap-3">
              <span className="inline-flex items-center rounded-md bg-[#7C3AED] text-white px-3 py-1.5 text-sm">Secure</span>
              <span className="inline-flex items-center rounded-md bg-[#0D9488] text-white px-3 py-1.5 text-sm">Fast</span>
              <span className="inline-flex items-center rounded-md bg-black/10 text-[#111827] px-3 py-1.5 text-sm">Private</span>
            </div>
          </div>
        </div>

        {/* Right form panel */}
        <div className="bg-white flex items-center justify-center p-8 md:p-12 border-l border-gray-100">
          <div className="w-full max-w-sm">
            <div className="mb-6">
              <h2 className="text-2xl font-semibold text-[#111827]">Sign in</h2>
              <p className="text-sm text-[#374151] mt-1">to continue to ExpenseEase</p>
            </div>

            {error ? (
              <div className="mb-4 rounded-lg bg-red-50 border border-red-200 text-red-700 px-3 py-2 text-sm">
                {error}
              </div>
            ) : null}

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm text-[#374151] mb-1">Email</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  autoComplete="email"
                  className="w-full rounded-lg border border-gray-200 bg-white text-[#111827] placeholder-gray-400 px-3 py-2 outline-none focus:ring-2 focus:ring-[#7C3AED]/40 focus:border-[#7C3AED]/60 transition"
                  placeholder="you@example.com"
                />
              </div>
              <div>
                <label className="block text-sm text-[#374151] mb-1">Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  autoComplete="current-password"
                  className="w-full rounded-lg border border-gray-200 bg-white text-[#111827] placeholder-gray-400 px-3 py-2 outline-none focus:ring-2 focus:ring-[#7C3AED]/40 focus:border-[#7C3AED]/60 transition"
                  placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                />
              </div>

              <button
                type="submit"
                disabled={loading}
                className="w-full mt-2 rounded-lg bg-[#7C3AED] text-white font-medium py-2.5 shadow-sm hover:brightness-110 active:brightness-95 transition disabled:opacity-70 disabled:cursor-not-allowed"
              >
                {loading ? 'Logging inâ€¦' : 'Login'}
              </button>
            </form>

            <div className="mt-4 text-center text-sm text-[#374151]">
              Don't have an account? <a href="/signup" className="text-[#0D9488] hover:underline">Sign up</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
