import React, { useEffect, useMemo, useState } from 'react';
import NavBar from '../components/NavBar';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';
import { useAuth } from '../context/AuthContext';

function Dashboard() {
  const { token } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [transactions, setTransactions] = useState([]);

  const authHeaders = useMemo(() => ({
    'Content-Type': 'application/json',
    ...(token ? { Authorization: `Bearer ${token}` } : {}),
  }), [token]);

  const fetchTransactions = async () => {
    try {
      const res = await fetch('/api/transactions', { headers: authHeaders });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.message || 'Failed to load transactions');
      setTransactions(data);
    } catch (e) {
      setError(e.message);
    }
  };

  useEffect(() => {
    if (token) {
      fetchTransactions();
    } else {
      setError('Please log in to view dashboard');
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token]);

  // Calculate metrics
  const metrics = useMemo(() => {
    const now = new Date();
    const currentMonth = now.getMonth();
    const currentYear = now.getFullYear();
    
    // Filter current month transactions
    const currentMonthTransactions = transactions.filter(t => {
      // Handle both string and Date objects
      const transactionDate = typeof t.date === 'string' ? new Date(t.date) : t.date;
      const isCurrentMonth = transactionDate.getMonth() === currentMonth && 
             transactionDate.getFullYear() === currentYear;
      console.log('Transaction:', t.date, 'Parsed:', transactionDate, 'Current month:', currentMonth, 'Current year:', currentYear, 'Is current month:', isCurrentMonth);
      return isCurrentMonth;
    });

    console.log('All transactions:', transactions);
    console.log('Current month transactions:', currentMonthTransactions);

    // Calculate totals - temporarily use all transactions for debugging
    const transactionsToUse = currentMonthTransactions.length > 0 ? currentMonthTransactions : transactions;
    console.log('Using transactions:', transactionsToUse);
    
    const incomeTransactions = transactionsToUse.filter(t => t.amount >= 0);
    const expenseTransactions = transactionsToUse.filter(t => t.amount < 0);
    
    console.log('Income transactions:', incomeTransactions);
    console.log('Expense transactions:', expenseTransactions);
    
    const totalIncome = incomeTransactions.reduce((sum, t) => sum + t.amount, 0);
    const totalExpenses = expenseTransactions.reduce((sum, t) => sum + Math.abs(t.amount), 0);
    const monthlySavings = totalIncome - totalExpenses;
    
    console.log('Total income:', totalIncome, 'Total expenses:', totalExpenses, 'Monthly savings:', monthlySavings);
    
    // If no current month transactions, show all transactions for debugging
    if (currentMonthTransactions.length === 0 && transactions.length > 0) {
      console.log('No current month transactions found, showing all transactions for debugging');
      const allIncome = transactions.filter(t => t.amount >= 0).reduce((sum, t) => sum + t.amount, 0);
      const allExpenses = transactions.filter(t => t.amount < 0).reduce((sum, t) => sum + Math.abs(t.amount), 0);
      console.log('All time income:', allIncome, 'All time expenses:', allExpenses);
    }

    // Calculate previous month for comparison
    const prevMonth = currentMonth === 0 ? 11 : currentMonth - 1;
    const prevYear = currentMonth === 0 ? currentYear - 1 : currentYear;
    
    const prevMonthTransactions = transactions.filter(t => {
      const transactionDate = new Date(t.date);
      return transactionDate.getMonth() === prevMonth && 
             transactionDate.getFullYear() === prevYear;
    });

    const prevMonthIncome = prevMonthTransactions
      .filter(t => t.amount >= 0)
      .reduce((sum, t) => sum + t.amount, 0);

    const prevMonthExpenses = prevMonthTransactions
      .filter(t => t.amount < 0)
      .reduce((sum, t) => sum + Math.abs(t.amount), 0);

    const prevMonthSavings = prevMonthIncome - prevMonthExpenses;

    // Calculate savings change
    const savingsChange = prevMonthSavings !== 0 
      ? ((monthlySavings - prevMonthSavings) / Math.abs(prevMonthSavings)) * 100
      : 0;

    // Top expense categories
    const expenseCategories = currentMonthTransactions
      .filter(t => t.amount < 0)
      .reduce((acc, t) => {
        acc[t.category] = (acc[t.category] || 0) + Math.abs(t.amount);
        return acc;
      }, {});

    const topExpenseCategories = Object.entries(expenseCategories)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 5);

    // Recent transactions
    const recentTransactions = transactions.slice(0, 5);

    return {
      totalIncome,
      totalExpenses,
      monthlySavings,
      savingsChange,
      topExpenseCategories,
      recentTransactions,
      transactionCount: currentMonthTransactions.length
    };
  }, [transactions]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getCategoryLabel = (category) => {
    const categories = {
      'salary': 'Salary',
      'bonus': 'Bonus',
      'freelance': 'Freelance',
      'investment': 'Investment',
      'other-income': 'Other Income',
      'groceries': 'Groceries',
      'rent': 'Rent',
      'utilities': 'Utilities',
      'transport': 'Transport',
      'entertainment': 'Entertainment',
      'health': 'Health',
      'other-expense': 'Other Expense'
    };
    return categories[category] || category;
  };

  const getPaymentMethodIcon = (method) => {
    const icons = {
      'cash': 'üíµ',
      'card': 'üí≥',
      'bank_transfer': 'üè¶',
      'digital_wallet': 'üì±',
      'other': 'üîß'
    };
    return icons[method] || 'üíµ';
  };

  if (loading) {
    return (
      <div className="relative min-h-screen w-full bg-[#F9FAFB]">
        <NavBar />
        <div className="flex items-center justify-center h-96">
          <div className="text-lg text-gray-600">Loading dashboard...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="relative min-h-screen w-full bg-[#F9FAFB]">
      <NavBar />
      <div className="relative mx-auto max-w-6xl px-4 py-8">
        <div className="grid grid-cols-1 md:grid-cols-[240px_1fr] gap-6">
          <Sidebar />

          <main>
            <div className="mb-8">
              <h1 className="text-3xl font-bold text-[#111827] mb-2">Dashboard</h1>
              <p className="text-gray-600">Welcome back! Here's your financial overview.</p>
            </div>

            {error ? (
              <div className="mb-6 rounded-lg bg-red-50 border border-red-200 text-red-700 px-4 py-3">
                {error}
              </div>
            ) : null}

            {/* Key Metrics Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              {/* Total Income */}
              <div className="bg-gradient-to-br from-emerald-50 to-emerald-100 rounded-2xl p-6 border border-emerald-200">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-emerald-600 text-sm font-medium">Total Income</p>
                    <p className="text-2xl font-bold text-emerald-800">{formatCurrency(metrics.totalIncome)}</p>
                    <p className="text-emerald-600 text-xs">This month</p>
                  </div>
                  <div className="w-12 h-12 bg-emerald-200 rounded-full flex items-center justify-center">
                    <span className="text-2xl">üí∞</span>
                  </div>
                </div>
              </div>

              {/* Total Expenses */}
              <div className="bg-gradient-to-br from-rose-50 to-rose-100 rounded-2xl p-6 border border-rose-200">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-rose-600 text-sm font-medium">Total Expenses</p>
                    <p className="text-2xl font-bold text-rose-800">{formatCurrency(metrics.totalExpenses)}</p>
                    <p className="text-rose-600 text-xs">This month</p>
                  </div>
                  <div className="w-12 h-12 bg-rose-200 rounded-full flex items-center justify-center">
                    <span className="text-2xl">üí∏</span>
                  </div>
                </div>
              </div>

              {/* Monthly Savings */}
              <div className={`bg-gradient-to-br rounded-2xl p-6 border ${
                metrics.monthlySavings >= 0 
                  ? 'from-emerald-50 to-emerald-100 border-emerald-200' 
                  : 'from-rose-50 to-rose-100 border-rose-200'
              }`}>
                <div className="flex items-center justify-between">
                  <div>
                    <p className={`text-sm font-medium ${
                      metrics.monthlySavings >= 0 ? 'text-emerald-600' : 'text-rose-600'
                    }`}>Monthly Savings</p>
                    <p className={`text-2xl font-bold ${
                      metrics.monthlySavings >= 0 ? 'text-emerald-800' : 'text-rose-800'
                    }`}>{formatCurrency(metrics.monthlySavings)}</p>
                    <p className={`text-xs ${
                      metrics.monthlySavings >= 0 ? 'text-emerald-600' : 'text-rose-600'
                    }`}>
                      {metrics.savingsChange > 0 ? '+' : ''}{metrics.savingsChange.toFixed(1)}% vs last month
                    </p>
                  </div>
                  <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
                    metrics.monthlySavings >= 0 ? 'bg-emerald-200' : 'bg-rose-200'
                  }`}>
                    <span className="text-2xl">{metrics.monthlySavings >= 0 ? 'üìà' : 'üìâ'}</span>
                  </div>
                </div>
              </div>

              {/* Transaction Count */}
              <div className="bg-gradient-to-br from-purple-50 to-purple-100 rounded-2xl p-6 border border-purple-200">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-purple-600 text-sm font-medium">Transactions</p>
                    <p className="text-2xl font-bold text-purple-800">{metrics.transactionCount}</p>
                    <p className="text-purple-600 text-xs">This month</p>
                  </div>
                  <div className="w-12 h-12 bg-purple-200 rounded-full flex items-center justify-center">
                    <span className="text-2xl">üìä</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Top Expense Categories */}
              <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-200">
                <h3 className="text-lg font-semibold text-[#111827] mb-4">Top Expense Categories</h3>
                {metrics.topExpenseCategories.length === 0 ? (
                  <p className="text-gray-500 text-sm">No expenses this month</p>
                ) : (
                  <div className="space-y-3">
                    {metrics.topExpenseCategories.map(([category, amount], index) => (
                      <div key={category} className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                          <div className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center text-sm font-medium">
                            {index + 1}
                          </div>
                          <span className="text-sm font-medium text-gray-700 capitalize">
                            {getCategoryLabel(category)}
                          </span>
                        </div>
                        <span className="text-sm font-semibold text-gray-900">
                          {formatCurrency(amount)}
                        </span>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Recent Transactions */}
              <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-200">
                <h3 className="text-lg font-semibold text-[#111827] mb-4">Recent Transactions</h3>
                {metrics.recentTransactions.length === 0 ? (
                  <p className="text-gray-500 text-sm">No transactions yet</p>
                ) : (
                  <div className="space-y-3">
                    {metrics.recentTransactions.map((transaction) => (
                      <div key={transaction._id} className="flex items-center justify-between py-2">
                        <div className="flex items-center gap-3">
                          <div className={`w-8 h-8 rounded-full flex items-center justify-center text-xs font-medium ${
                            transaction.type === 'income' 
                              ? 'bg-emerald-100 text-emerald-700' 
                              : 'bg-rose-100 text-rose-700'
                          }`}>
                            {transaction.type === 'income' ? '+' : '-'}
                          </div>
                          <div>
                            <p className="text-sm font-medium text-gray-900 capitalize">
                              {getCategoryLabel(transaction.category)}
                            </p>
                            <p className="text-xs text-gray-500 flex items-center gap-1">
                              <span>{getPaymentMethodIcon(transaction.paymentMethod)}</span>
                              <span>{new Date(transaction.date).toLocaleDateString()}</span>
                            </p>
                          </div>
                        </div>
                        <div className="text-right">
                          <p className={`text-sm font-semibold ${
                            transaction.amount >= 0 ? 'text-emerald-600' : 'text-rose-600'
                          }`}>
                            {transaction.amount >= 0 ? '+' : ''}{formatCurrency(transaction.amount)}
                          </p>
                          {transaction.note && (
                            <p className="text-xs text-gray-500 truncate max-w-24">
                              {transaction.note}
                            </p>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Monthly Savings Chart Placeholder */}
            <div className="mt-8 bg-white rounded-2xl p-6 shadow-sm border border-gray-200">
              <h3 className="text-lg font-semibold text-[#111827] mb-4">Savings Trend</h3>
              <div className="h-32 flex items-center justify-center bg-gray-50 rounded-lg">
                <div className="text-center">
                  <div className="text-4xl mb-2">üìà</div>
                  <p className="text-gray-500 text-sm">Chart visualization coming soon</p>
                </div>
              </div>
            </div>
          </main>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Dashboard;
