import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const NavBar = () => {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <header className="sticky top-0 z-30 backdrop-blur-xl bg-black/30 border-b border-white/10">
      <div className="mx-auto max-w-6xl px-4 h-14 flex items-center justify-between">
        <Link to="/" className="flex items-center gap-2">
          <div className="w-3 h-3 rounded-sm bg-[#FD980C]" />
          <span className="font-semibold text-white">ExpenseTracker</span>
        </Link>
        <nav className="flex items-center gap-4 text-sm">
          <Link to="/" className="text-white/80 hover:text-white transition">Home</Link>
          <Link to="/dashboard" className="text-white/80 hover:text-white transition">Dashboard</Link>
          {isAuthenticated ? (
            <button onClick={handleLogout} className="inline-flex items-center rounded-md bg-white/10 px-3 py-1.5 text-white font-medium border border-white/20 hover:bg-white/20 transition">Logout</button>
          ) : (
            <Link to="/login" className="inline-flex items-center rounded-md bg-[#FD980C] px-3 py-1.5 text-black font-medium shadow hover:shadow-black/30 transition">Login</Link>
          )}
        </nav>
      </div>
    </header>
  );
};

export default NavBar;
